export * from './api';
export * from './crypto-js';

import {StripeOnramp, StripeOnrampConstructor} from './crypto-js';

export const loadStripeOnramp: (
  publishableKey: string
) => Promise<StripeOnramp | null>;

declare global {
  interface Window {
    // Stripe Onramp must be loaded directly from https://crypto-js.stripe.com, which
    // places a `StripeOnramp` object on the window
    StripeOnramp?: StripeOnrampConstructor;
  }
}
